    try {
      let page = 1;
      if (change == "next") page = pageNumber + 1;
      else if (change == "prev" && pageNumber > 1) page = pageNumber - 1;
      else page = pageNumber;
      const response = await axios.get(
        `https://www.anapioficeandfire.com/api/houses/?page=${page}`
