import { ActionTypes } from "../constants/action-types";

const initialState = {
  houses: [],
  pageNumber: 1,
};

export const houseReducer = (
  state = initialState,
  { type, payload, pageNo }
) => {
  switch (type) {
    case ActionTypes.SET_HOUSES:
      return { ...state, houses: payload };
    case ActionTypes.NEXT_PAGE:
      return { ...state, houses: payload, pageNumber: pageNo };
    case ActionTypes.PREVIOUS_PAGE:
      return { ...state, houses: payload };
    default:
      return state;
  }
};

export const selectedHouseReducer = (state = {}, { type, payload }) => {
  switch (type) {
    case ActionTypes.SELECTED_HOUSES:
      return { ...state, ...payload };
    case ActionTypes.REMOVE_SELECTED_HOUSES:
      return {};
    default:
      return state;
  }
};

export const changePage = (state = initialState, { type, payload }) => {
  switch (type) {
    case ActionTypes.NEXT_PAGE:
      return { ...state, houses: payload, pageNumber: pageNumber };
  }
};
