import React, { useEffect } from "react";
import axios from "axios";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { selectedHouses } from "../redux/actions/houseActions";

function HouseDetails() {
  const house = useSelector((state) => state.houses);
  const { name, region, coatOfArms, words, url } = house;
  console.log(house);
  const { houseId } = useParams();
  const dispatch = useDispatch();

  const fetchHouseDetails = async () => {
    const response = await axios
      .get(`https://www.anapioficeandfire.com/api/houses/${houseId}`)
      .catch((err) => {
        console.log("Err", err);
      });
    dispatch(selectedHouses(response.data));
  };

  useEffect(() => {
    if (houseId && houseId !== "") fetchHouseDetails();
  }, [houseId]);
  return (
    <div>
      {Object.keys(house).length === 0 ? (
        <div>...Loading</div>
      ) : (
        <div>
          <ul>
            <li>{name}</li>
            <ul>
              <li>{region ? region : "No Information"}</li>
              <li>{coatOfArms ? coatOfArms : "No Information"}</li>
              <li>{words ? words : "No Information"}</li>
            </ul>
          </ul>
        </div>
      )}
    </div>
  );
}
export default HouseDetails;
